mapper.update:
UPDATE users
SET photoid=#{photoid}
WHERE email=#{email}

mapper.insert:
INSERT INTO users (
email, username, password, joindate, goal, goalsuccess, height, age, gender, activity, photoid, successdate
) VALUES (
#{email}, #{username}, #{password}, sysdate(),
#{goal}, #{goalsuccess}, #{height}, #{age}, #{gender}, #{activity}, null, null
)

mapper.update:
		UPDATE users 
 		SET height = #{height}, goal = #{goal}, activity = #{activity}
 		WHERE email = #{email}
